import * as React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import MagicCard from '../components/MagicCard'
import SearchBar from '../components/SearchBar'


const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  toolbar: {
    paddingRight: 24, 
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    backgroundColor: '#778899',
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  },
  fixedHeight: {
    height: 250,
  }
}));

const Search = (props) => {
    const classes = useStyles();

    const [count, setCount ] = React.useState(0)
    const [cards,setCards] = React.useState([]);
    const [filteredCards,setFilter] = React.useState([]);
    const [searchTerm, setSearchTerm] = React.useState('')

    React.useEffect(() => {
        const fetchCards = () => {
            fetch(`http://localhost:3001/cards`)
        .then(res=>res.json())
        .then(cardCollection=> setCards(cardCollection))
        }
        fetchCards();
    },[count]
    )

    const filterCards = ()=>{
       const filter = cards.filter(card => card.name.includes('Abundance'))
       console.log(filter)
       setFilter(filter)
    }

    return (
        <main className={classes.content}>
            <div className={classes.appBarSpacer} />
                <Container maxWidth="lg" className={classes.container}>
                    <SearchBar filterCards={filterCards} className={classes.searchBar}/>
                    <Grid container spacing={1}>
                        {
                            cards.map(card => 
                                <Grid key={card.id} item xs={12} md={4} lg={3}>
                                        <MagicCard card={cards}/>
                                </Grid>
                            )
                        }
                    </Grid>
                </Container>
        </main>
    );
}

export default Search;
